import React from 'react';
import './App.css';
import ItemForm from './components/ItemForm';
import Items from './components/Items';

function App() {
  const [todos, setTodos] = React.useState(['Todo 1','Todo 2']);

  const onAdd = () => {
    console.log('onAdd called')
    setTodos((todos) =>[...todos, 'new'])
  }

  const onDeleteHandler = (i) => {
    setTodos((todos) =>[...todos.slice(0,i), ...todos.slice(i + 1)])
  }

  return (
    <div>
      <h1>Todos</h1>
      <ItemForm />
      <Items todos={todos} onDelete={onDeleteHandler} />
    </div>
  )
}

export default App;
