import React from 'react';
import './App.css';
import ItemForm from './components/ItemForm';
import Items from './components/Items';

function App() {
  const [todos, setTodos] = React.useState(['Todo 1','Todo 2']);

  const onAdd = () => {
    console.log('onAdd called')
    setTodos((todos) =>[...todos, 'new'])
  }

  const onPop = () => {
    console.log('onPop called')
    setTodos((todos) =>[...todos.slice(0, todos.length - 1)])
  }

  return (
    <div>
      <h1>Todos</h1>
      <p>
        <button onClick={onAdd}>+</button>
        <button onClick={onPop}>-</button>
      </p>
      <ItemForm />
      <Items todos={todos} />
    </div>
  )
}

export default App;
