import React from 'react';
import './App.css';
import ItemForm from './components/ItemForm';
import Items from './components/Items';

function App() {
  const [todos, setTodos] = React.useState(['Todo 1','Todo 2']);
  const [editIndex, setEditIndex] = React.useState(-1)

  const onDeleteHandler = (i) => {
    setTodos((todos) =>[...todos.slice(0,i), ...todos.slice(i + 1)])
  }

  const onAddHandler = (todo) =>{
    if(editIndex == -1){
      setTodos(todos => [...todos,todo])
    }else{
      setTodos((todos) =>[...todos.slice(0,i),todo, ...todos.slice(i + 1)])
    }
  }

  const onEditHandler =(i) =>{
      setEditIndex(i)
  }

  return (
    <div>
      <h1>Todos</h1>
      <ItemForm onAdd={onAddHandler} editValue={todos[editIndex]} />
      <Items todos={todos} onDelete={onDeleteHandler} onEdit={onEditHandler}/>
    </div>
  )
}

export default App;
